const arrowLeft=document.querySelector(".arrow-left"),arrowRight=document.querySelector(".arrow-right"),slides=document.querySelectorAll(".slider-image"),bottom=document.getElementById("bottom");let currentSlideIndex=0;const paginationCircles=[];function createPaginationCircle(){var e=document.createElement("div");e.className="pagination-circle",bottom.appendChild(e),paginationCircles.push(e)}function addPagination(){slides.forEach(createPaginationCircle),paginationCircles[0].classList.add("active"),paginationCircles.forEach((e,i)=>{e.addEventListener("click",()=>changeSlide(i))})}function showSlide(){slides[currentSlideIndex].classList.add("block"),paginationCircles[currentSlideIndex].classList.add("active")}function hideSlide(){paginationCircles[currentSlideIndex].classList.remove("active"),slides[currentSlideIndex].classList.remove("block")}function changeSlide(e){hideSlide(),currentSlideIndex=e,showSlide(),checkArrows()}function checkArrows(){arrowRight.style.visibility="visible",arrowLeft.style.visibility="visible",0==currentSlideIndex?arrowLeft.style.visibility="hidden":currentSlideIndex==slides.length-1&&(arrowRight.style.visibility="hidden")}function nextSlide(){let e=currentSlideIndex+1;changeSlide(e=e>slides.length-1?0:e)}function previousSlide(){let e=currentSlideIndex-1;changeSlide(e=e<0?slides.length-1:e)}addPagination(),arrowLeft.addEventListener("click",previousSlide),arrowRight.addEventListener("click",nextSlide),checkArrows();